    public static void printAllIdProfessors(){
        ResultSet rs;
        String sql = "SELECT id FROM professor;";
        rs = simpleQuerySchema(sql);
        try {
            while (rs.next()) {
                System.err.println(rs.getInt(1));
            }
            rs.close();
        }
        catch (Exception e){
            closeConnection();
            exit(5);
        }
    }

    public static void printProfessorWithDepartment(int num){
        ResultSet rs;
        String sql = "SELECT id, address FROM professor WHERE department = " + num +";";
        rs = simpleQuerySchema(sql);
        try {
            while (rs.next()){
                System.err.println(rs.getInt(1) + ", " + rs.getString("address"));
            }
            rs.close();
        }
        catch (Exception e){
            closeConnection();
            exit(5);
        }
    }

